using System;\nusing System.Threading.Tasks;\nusing SendGrid;\nusing SendGrid.Helpers.Mail;\n\npublic class EmailService\n{\n    private const string SendGridApiKey = "YOUR_SENDGRID_API_KEY";\n\n    public static async Task SendEmailAsync(string toEmail, string subject, string message)\n    {\n        var client = new SendGridClient(SendGridApiKey);\n        var from = new EmailAddress("your-email@example.com", "Your Name");\n        var to = new EmailAddress(toEmail);\n        var msg = MailHelper.CreateSingleEmail(from, to, subject, message, message);\n\n        try\n        {\n            var response = await client.SendEmailAsync(msg);\n            Console.WriteLine($"Email sent with status code: {response.StatusCode}");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"An error occurred: {ex.Message}");\n        }\n    }\n}