public void DirSearch(string sDir)\n{\n    try\n    {\n        foreach (string d in Directory.GetDirectories(sDir))\n        {\n            DirectoryInfo dInfo = new DirectoryInfo(d);\n            FileAttributes attributes = dInfo.Attributes;\n\n            if ((attributes & FileAttributes.ReparsePoint) == 0)\n            {\n                foreach (string f in Directory.GetFiles(d, searchString))\n                {\n                    //lstFilesFound.Items.Add(f);\n                    ListViewItem lvi;\n                    ListViewItem.ListViewSubItem lvsi;\n                    lvi = new ListViewItem();\n                    lvi.Text = f;\n                    lvi.ImageIndex = 1;\n                    lvi.Tag = "tag";\n                    lvsi = new ListViewItem.ListViewSubItem();\n                    lvsi.Text = "sub bugger";\n                    lvi.SubItems.Add(lvsi);\n\n                    lvsi = new ListViewItem.ListViewSubItem();\n                    lvsi.Text = d;\n                    lvi.SubItems.Add(lvsi);\n\n                    listView1.Items.Add(lvi);\n                }\n                DirSearch(d);\n            }\n        }\n    }\n    catch (UnauthorizedAccessException ex)\n    {\n        // Handle the exception, e.g., log it or display a message\n        Console.WriteLine($"Access to the path '{sDir}' is denied: {ex.Message}");\n    }\n    catch (Exception ex)\n    {\n        // Handle other possible exceptions\n        Console.WriteLine($"An error occurred: {ex.Message}");\n    }\n}