var result = from person in persons\n             join personInCommunity in tablePersonInCommunity\n             on person.PersonID equals personInCommunity.PersonID\n             join personInContact in tablePersonInContact\n             on person.PersonID equals personInContact.PersonID\n             join contact in contacts\n             on personInContact.ContactID equals contact.ContactID\n             group contact by new { person.PersonID } into g\n             select new\n             {\n                 PersonID = g.Key.PersonID,\n                 BusinessContact = g.FirstOrDefault(c => c.ContactType == Business)?.Address1,\n                 HomeContact = g.FirstOrDefault(c => c.ContactType == Home)?.Address1\n             };