using System;\nusing System.Windows.Forms;\n\npublic class WebBrowserAutomation\n{\n    private WebBrowser webBrowser;\n\n    public WebBrowserAutomation()\n    {\n        webBrowser = new WebBrowser();\n        webBrowser.ScriptErrorsSuppressed = true;\n        webBrowser.DocumentCompleted += new WebBrowserDocumentCompletedEventHandler(WebBrowser_DocumentCompleted);\n        webBrowser.Navigate("http://example.com"); // Replace with the target URL\n    }\n\n    private void WebBrowser_DocumentCompleted(object sender, WebBrowserDocumentCompletedEventArgs e)\n    {\n        if (webBrowser.ReadyState == WebBrowserReadyState.Complete)\n        {\n            HtmlElement inputElement = webBrowser.Document.GetElementById("inputFieldId"); // Replace with the actual input field id\n\n            if (inputElement != null)\n            {\n                inputElement.Focus();\n                webBrowser.Document.InvokeScript("eval", new object[]\n                {\n                    "document.getElementById('inputFieldId').value = 'your input';" + // Replace 'your input' with the desired input\n                    "var event = new KeyboardEvent('keypress', {'key': 'Enter'});" +\n                    "document.getElementById('inputFieldId').dispatchEvent(event);"\n                });\n            }\n        }\n    }\n\n    public static void Main()\n    {\n        Application.Run(new WebBrowserAutomation());\n    }\n}