using System;\nusing System.Messaging;\n\npublic class MSMQExample\n{\n    public static void Main()\n    {\n        string queuePath = @".\private$\yourQueueName"; // Replace with your queue name\n        string messageId = "yourMessageId"; // Replace with the message ID you want to delete\n\n        try\n        {\n            if (MessageQueue.Exists(queuePath))\n            {\n                using (MessageQueue queue = new MessageQueue(queuePath))\n                {\n                    // Receive the message with the specified ID\n                    Message message = queue.ReceiveById(messageId);\n                    Console.WriteLine("Message with ID {0} has been deleted.", messageId);\n                }\n            }\n            else\n            {\n                Console.WriteLine("Queue does not exist.");\n            }\n        }\n        catch (MessageQueueException mqex)\n        {\n            Console.WriteLine("MessageQueueException: {0}", mqex.Message);\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Exception: {0}", ex.Message);\n        }\n    }\n}