private static void FindItemInList(string searchString)\n{\n    List<string> list = new List<string> { "Box", "Gate", "Car" };\n    int itemId = list.FindIndex(item => item.Equals(searchString, StringComparison.OrdinalIgnoreCase));\n    Console.WriteLine(itemId);\n}\n\npublic static void Main(string[] args)\n{\n    string searchString = "box"; // This can be user-entered\n    FindItemInList(searchString);\n}