XmlDocument myXmlDoc = new XmlDocument();\nmyXmlDoc.LoadXml(@"\n<My_RootNode xmlns:xsi='http://www.w3.org/2001/XMLSchema-instance' xmlns:xsd='http://www.w3.org/2001/XMLSchema' xmlns='urn:hl7-org:v3'>\n  <id root='2.16.840.1.113883.3.51.1.1.1' extension='someIdentifier' />\n  <creationTime xsi:nil='true' />\n</My_RootNode>");\n\nXmlNamespaceManager nsmgr = new XmlNamespaceManager(myXmlDoc.NameTable);\nnsmgr.AddNamespace("hl7", "urn:hl7-org:v3");\n\nXmlNode idNode = myXmlDoc.SelectSingleNode("/hl7:My_RootNode/hl7:id", nsmgr);\nif (idNode != null)\n{\n    string msgID = idNode.Attributes["extension"].Value;\n    Console.WriteLine(msgID);\n}\nelse\n{\n    Console.WriteLine("Node not found");\n}