@using GridMvc.Html\n\n@{\n    var grid = new GridMvc.Grid<YourModel>(Model)\n        .Columns(columns =>\n        {\n            columns.Add(c => c.Property1).Titled("Property 1");\n            columns.Add(c => c.Property2).Titled("Property 2");\n            // Add more columns as needed\n        })\n        .WithPaging(10) // Page size\n        .Sortable(true);\n}\n\n@grid.Render()