using System;\nusing System.Diagnostics;\nusing System.Linq;\nusing MyApp;\n\nclass Program\n{\n    static void Main()\n    {\n        // Get the list of processes before instantiation\n        var processesBefore = Process.GetProcessesByName("myapp").Select(p => p.Id).ToList();\n\n        // Instantiate the COM object\n        ApplicationClass anApp = new ApplicationClass();\n\n        // Get the list of processes after instantiation\n        var processesAfter = Process.GetProcessesByName("myapp").Select(p => p.Id).ToList();\n\n        // Find the new process ID\n        var newPid = processesAfter.Except(processesBefore).FirstOrDefault();\n\n        if (newPid != 0)\n        {\n            Console.WriteLine("New process ID: " + newPid);\n        }\n        else\n        {\n            Console.WriteLine("No new process found.");\n        }\n    }\n}