using System;\nusing System.Linq;\nusing System.Xml.Linq;\n\npublic class LastFmXmlParser\n{\n    public void ParseResponse(string xmlResponse)\n    {\n        try\n        {\n            // Parse the XML response string into an XDocument\n            XDocument xmlDoc = XDocument.Parse(xmlResponse);\n\n            // Example: Access the first "artist" element and its "name" child element\n            XElement artistElement = xmlDoc.Descendants("artist").FirstOrDefault();\n            if (artistElement != null)\n            {\n                XElement nameElement = artistElement.Element("name");\n                if (nameElement != null)\n                {\n                    string artistName = nameElement.Value;\n                    Console.WriteLine("Artist Name: " + artistName);\n                }\n                else\n                {\n                    Console.WriteLine("Name element not found.");\n                }\n            }\n            else\n            {\n                Console.WriteLine("Artist element not found.");\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Error parsing XML: " + ex.Message);\n        }\n    }\n}\n\npublic class Program\n{\n    public static void Main()\n    {\n        string xmlResponse = @"<?xml version=""1.0"" encoding=""utf-8""?>\n                                <lfm>\n                                    <artist>\n                                        <name>Air</name>\n                                        <mbid>cb67438a-7f50-4f2b-a6f1-2bb2729fd538</mbid>\n                                        <url>http://www.last.fm/music/Air</url>\n                                        <image>http://userserve-ak.last.fm/serve/34/251119.jpg</image>\n                                        <streamable>1</streamable>\n                                        <ontour>0</ontour>\n                                        <stats>\n                                            <listeners>1176515</listeners>\n                                            <playcount>39388973</playcount>\n                                        </stats>\n                                    </artist>\n                                </lfm>";\n\n        LastFmXmlParser parser = new LastFmXmlParser();\n        parser.ParseResponse(xmlResponse);\n    }\n}