using System;\nusing System.Net;\nusing System.IO;\nusing System.Windows.Forms;\n\npublic class ProxyForm : Form\n{\n    private TextBox proxyAddressTextBox;\n    private TextBox proxyUsernameTextBox;\n    private TextBox proxyPasswordTextBox;\n    private Button connectButton;\n\n    public ProxyForm()\n    {\n        proxyAddressTextBox = new TextBox { PlaceholderText = "Proxy Address" };\n        proxyUsernameTextBox = new TextBox { PlaceholderText = "Username" };\n        proxyPasswordTextBox = new TextBox { PlaceholderText = "Password", PasswordChar = '*' };\n        connectButton = new Button { Text = "Connect" };\n\n        connectButton.Click += ConnectButton_Click;\n\n        Controls.Add(proxyAddressTextBox);\n        Controls.Add(proxyUsernameTextBox);\n        Controls.Add(proxyPasswordTextBox);\n        Controls.Add(connectButton);\n    }\n\n    private void ConnectButton_Click(object sender, EventArgs e)\n    {\n        string proxyAddress = proxyAddressTextBox.Text;\n        string proxyUsername = proxyUsernameTextBox.Text;\n        string proxyPassword = proxyPasswordTextBox.Text;\n\n        WebProxy proxy = new WebProxy(proxyAddress);\n        proxy.Credentials = new NetworkCredential(proxyUsername, proxyPassword);\n\n        WebRequest request = WebRequest.Create("http://www.example.com");\n        request.Proxy = proxy;\n\n        try\n        {\n            using (WebResponse response = request.GetResponse())\n            {\n                using (StreamReader reader = new StreamReader(response.GetResponseStream()))\n                {\n                    string responseText = reader.ReadToEnd();\n                    MessageBox.Show(responseText, "Response");\n                }\n            }\n        }\n        catch (Exception ex)\n        {\n            MessageBox.Show(ex.Message, "Error");\n        }\n    }\n}