using System;\nusing System.Diagnostics;\nusing System.Management;\n\npublic class ComDllInfo\n{\n    public static string GetFileVersion(string progID)\n    {\n        string filePath = GetDllPathFromProgID(progID);\n        if (!string.IsNullOrEmpty(filePath))\n        {\n            FileVersionInfo fileVersionInfo = FileVersionInfo.GetVersionInfo(filePath);\n            return fileVersionInfo.FileVersion;\n        }\n        return null;\n    }\n\n    private static string GetDllPathFromProgID(string progID)\n    {\n        string query = $"SELECT LocalServer32 FROM Win32_ProgIDSpecification WHERE ProgID = '{progID}'";\n        \n        using (ManagementObjectSearcher searcher = new ManagementObjectSearcher(query))\n        {\n            foreach (ManagementObject obj in searcher.Get())\n            {\n                if (obj["LocalServer32"] != null)\n                {\n                    return obj["LocalServer32"].ToString();\n                }\n            }\n        }\n        \n        return null;\n    }\n}