protected void SubmitButton_Click(object sender, EventArgs e)\n{\n    try\n    {\n        // Define the file path\n        string filePath = Server.MapPath("~/Pics/Image1.jpg");\n        \n        // Check if the file exists\n        if (System.IO.File.Exists(filePath))\n        {\n            // Delete the file\n            System.IO.File.Delete(filePath);\n            // Optionally, inform the user that the file was deleted\n            Response.Write("File deleted successfully.");\n        }\n        else\n        {\n            // Inform the user that the file does not exist\n            Response.Write("File not found.");\n        }\n    }\n    catch (Exception ex)\n    {\n        // Handle any exceptions that occur during the file deletion\n        Response.Write("Error: " + ex.Message);\n    }\n}