using System;\n   using System.Data.SqlClient;\n   using System.Diagnostics;\n\n   namespace DatabaseInstaller\n   {\n       class Program\n       {\n           static void Main(string[] args)\n           {\n               string connectionString = "Server=localhost\\SQLEXPRESS;Integrated Security=true;";\n               string backupFilePath = "C:\\Path\\To\\YourDatabase.bak";\n\n               RestoreDatabase(connectionString, "YourDatabase", backupFilePath);\n           }\n\n           static void RestoreDatabase(string connectionString, string databaseName, string backupFilePath)\n           {\n               using (SqlConnection connection = new SqlConnection(connectionString))\n               {\n                   connection.Open();\n                   string sql = $@"\n                       RESTORE DATABASE [{databaseName}]\n                       FROM DISK = N'{backupFilePath}'\n                       WITH FILE = 1,\n                       MOVE N'{databaseName}' TO N'C:\\Program Files\\Microsoft SQL Server\\MSSQL14.SQLEXPRESS\\MSSQL\\DATA\\{databaseName}.mdf',\n                       MOVE N'{databaseName}_Log' TO N'C:\\Program Files\\Microsoft SQL Server\\MSSQL14.SQLEXPRESS\\MSSQL\\DATA\\{databaseName}_Log.ldf',\n                       NOUNLOAD, REPLACE, STATS = 5";\n\n                   using (SqlCommand command = new SqlCommand(sql, connection))\n                   {\n                       command.ExecuteNonQuery();\n                       Console.WriteLine("Database restored successfully.");\n                   }\n               }\n           }\n       }\n   }