// Default.aspx.cs\nusing System;\nusing System.IO;\n\npublic partial class Default : System.Web.UI.Page\n{\n    protected void Page_PreInit(object sender, EventArgs e)\n    {\n        string filePath = Server.MapPath("~/Sample.htm");\n        if (File.Exists(filePath))\n        {\n            string html = File.ReadAllText(filePath);\n            htmlContent.InnerHtml = html;\n        }\n    }\n\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        if (!IsPostBack)\n        {\n            var myAnchor = htmlContent.FindControl("myAnchor") as HtmlAnchor;\n            if (myAnchor != null)\n            {\n                // Perform actions on the anchor element\n                myAnchor.HRef = "http://example.com";\n            }\n        }\n    }\n}