public class AccountController : Controller\n   {\n       public ActionResult Login()\n       {\n           return View();\n       }\n\n       [HttpPost]\n       public ActionResult Login(LoginViewModel model)\n       {\n           if (ModelState.IsValid)\n           {\n               // Check if the user exists in Active Directory\n               if (Membership.Providers["ADMembershipProvider"].ValidateUser(model.Username, model.Password))\n               {\n                   // Log the user in with Forms Authentication\n                   FormsAuthentication.SetAuthCookie(model.Username, model.RememberMe);\n                   return RedirectToAction("Index", "Home");\n               }\n               // Check if the user exists in SQL Membership Provider\n               else if (Membership.Providers["SQLMembershipProvider"].ValidateUser(model.Username, model.Password))\n               {\n                   // Log the user in with Forms Authentication\n                   FormsAuthentication.SetAuthCookie(model.Username, model.RememberMe);\n                   return RedirectToAction("Index", "Home");\n               }\n               else\n               {\n                   ModelState.AddModelError("", "Invalid username or password.");\n               }\n           }\n           return View(model);\n       }\n   }