class Program\n{\n    static void Main()\n    {\n        string connectionString = "Your Oracle Connection String";\n        string statement = "INSERT INTO PERSON (ID, NAME, BIRTHDATE) VALUES (:id, :name, :birthdate)";\n\n        OracleParameter[] parameters = {\n            new OracleParameter(":id", 23),\n            new OracleParameter(":name", "Mike"),\n            new OracleParameter(":birthdate", new DateTime(2003, 07, 09))\n        };\n\n        OracleHelper.ExecuteParameterizedQuery(connectionString, statement, parameters);\n    }\n}