using System;\n   using System.Data;\n   using Oracle.ManagedDataAccess.Client;\n\n   public class OracleHRDataAccess\n   {\n       private string connectionString = "User Id=<username>;Password=<password>;Data Source=<datasource>";\n\n       public DataTable GetEmployeeData()\n       {\n           using (OracleConnection conn = new OracleConnection(connectionString))\n           {\n               conn.Open();\n               OracleCommand cmd = new OracleCommand("SELECT * FROM Employee", conn);\n               OracleDataAdapter da = new OracleDataAdapter(cmd);\n\n               DataTable dt = new DataTable();\n               da.Fill(dt);\n               return dt;\n           }\n       }\n   }