using System;\nusing System.Threading;\n\nclass Program\n{\n    static void Main()\n    {\n        // Set data in the thread's data slot\n        LocalDataStoreSlot slot = Thread.GetNamedDataSlot("MyData");\n        Thread.SetData(slot, "Hello, World!");\n\n        // Retrieve data in the same thread\n        string data = (string)Thread.GetData(slot);\n        Console.WriteLine(data); // Output: Hello, World!\n    }\n}