using System;\nusing System.IO;\nusing System.Reflection;\n\nclass Program\n{\n    static void Main()\n    {\n        // Set the subdirectory where ODAC files are located\n        string odacDirectory = Path.Combine(AppDomain.CurrentDomain.BaseDirectory, "ODAC");\n\n        // Add the ODAC directory to the private path\n        AppDomain.CurrentDomain.AppendPrivatePath(odacDirectory);\n\n        // Load Oracle.DataAccess.dll from the ODAC directory\n        string oracleDllPath = Path.Combine(odacDirectory, "Oracle.DataAccess.dll");\n        Assembly.LoadFrom(oracleDllPath);\n\n        // Your application logic here\n    }\n}