using System;\nusing System.Net.Http;\nusing System.Text.RegularExpressions;\nusing System.Threading.Tasks;\n\nclass Program\n{\n    static async Task Main(string[] args)\n    {\n        string url = "http://example.com"; // replace with your URL\n        string content = await LoadUrlContent(url);\n        MatchHtmlTags(content);\n    }\n\n    static async Task<string> LoadUrlContent(string url)\n    {\n        using(HttpClient client = new HttpClient())\n        {\n            try\n            {\n                return await client.GetStringAsync(url);\n            }\n            catch(Exception ex)\n            {\n                Console.WriteLine($"Error loading content: {ex.Message}");\n                return string.Empty;\n            }\n        }\n    }\n\n    static void MatchHtmlTags(string input)\n    {\n        string pattern = @"<.*?>"; // regex to match HTML tags\n        Regex regex = new Regex(pattern);\n\n        MatchCollection matches = regex.Matches(input);\n        foreach (Match match in matches)\n        {\n            Console.WriteLine(match.Value);\n        }\n    }\n}