private OracleDependency SubscribeToTable(string tableName)\n{\n    string sql = "select * from " + tableName;\n    var cmd = new OracleCommand(sql, this.connection) { AddRowid = true };\n    \n    // Initialize OracleDependency with the OracleCommand\n    var dep = new OracleDependency(cmd);\n    \n    // Ensure notifications continue to be sent\n    cmd.Notification.IsNotifiedOnce = false;\n    \n    // Subscribe to the OnChange event\n    dep.OnChange += this.dep_OnChange;\n    \n    // Execute the command to start listening for changes\n    cmd.ExecuteNonQuery();\n    \n    return dep;\n}