using System;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string filePath = @"C:\path\to\your\file.txt";\n        \n        // Check if file exists\n        if (File.Exists(filePath))\n        {\n            // Get the current attributes of the file\n            FileAttributes attributes = File.GetAttributes(filePath);\n            \n            // Remove the ReadOnly attribute if it is set\n            if ((attributes & FileAttributes.ReadOnly) == FileAttributes.ReadOnly)\n            {\n                attributes &= ~FileAttributes.ReadOnly;\n                File.SetAttributes(filePath, attributes);\n                Console.WriteLine("The file is now writable.");\n            }\n            else\n            {\n                Console.WriteLine("The file is already writable.");\n            }\n        }\n        else\n        {\n            Console.WriteLine("The file does not exist.");\n        }\n    }\n}