using System;\nusing System.Linq;\nusing System.Web.Hosting;\nusing Microsoft.Web.Administration;\n\npublic class IISHelper\n{\n    public static int GetIISSiteId()\n    {\n        string siteName = HostingEnvironment.SiteName;\n        using (ServerManager serverManager = new ServerManager())\n        {\n            var site = serverManager.Sites.FirstOrDefault(s => s.Name.Equals(siteName, StringComparison.OrdinalIgnoreCase));\n            return site?.Id ?? -1; // Returns -1 if site not found\n        }\n    }\n}