using System;\nusing System.Linq;\nusing System.Xml.Linq;\nusing System.Collections.Generic;\n\npublic class Program\n{\n    public static void Main(string[] args)\n    {\n        // Load the XML document\n        XDocument doc = XDocument.Load(@"path/to/your/xmlfile.xml");\n\n        // Group by att1 attribute and select the top 4 based on the count\n        var topItems = doc.Descendants("item")\n                          .GroupBy(item => (string)item.Attribute("att1"))\n                          .Select(group => new\n                          {\n                              Att1 = group.Key,\n                              Count = group.Count(),\n                              Att2 = group.First().Attribute("att2").Value\n                          })\n                          .OrderByDescending(x => x.Count)\n                          .Take(4)\n                          .ToList();\n\n        // Print the results\n        foreach (var item in topItems)\n        {\n            Console.WriteLine($"att1: {item.Att1}, Count: {item.Count}, att2: {item.Att2}");\n        }\n    }\n}