using System;\nusing System.Data;\nusing System.Xml;\n\nDataSet ds = new DataSet();\n// Assume ds is already populated with data\n\n// Specify the path to save the XML file\nstring strXmlTestCasePath = "path_to_your_file.xml";\n\n// Write the dataset to an XML file\nds.WriteXml(strXmlTestCasePath, XmlWriteMode.IgnoreSchema);\n\n// Load the generated XML file into an XmlDocument\nXmlDocument xmlDoc = new XmlDocument();\nxmlDoc.Load(strXmlTestCasePath);\n\n// Create a new element to add to the XML\nXmlElement newElement = xmlDoc.CreateElement("ExtraInfo");\nnewElement.InnerText = "This is some extra information";\n\n// Append the new element to the root node or any desired node\nxmlDoc.DocumentElement.AppendChild(newElement);\n\n// Save the modified XML back to the file\nxmlDoc.Save(strXmlTestCasePath);