using System;\nusing System.Collections.Generic;\nusing System.Web.UI.WebControls;\n\nnamespace YourNamespace\n{\n    public partial class UniqueValidation : System.Web.UI.Page\n    {\n        protected void Page_Load(object sender, EventArgs e)\n        {\n        }\n\n        protected void CustomValidator1_ServerValidate(object source, ServerValidateEventArgs args)\n        {\n            List<string> enteredTexts = new List<string>();\n            bool isUnique = true;\n\n            foreach (Control control in form1.Controls)\n            {\n                if (control is TextBox && control.ID.StartsWith("TextBox"))\n                {\n                    TextBox textBox = (TextBox)control;\n\n                    if (enteredTexts.Contains(textBox.Text))\n                    {\n                        isUnique = false;\n                        break;\n                    }\n                    else\n                    {\n                        enteredTexts.Add(textBox.Text);\n                    }\n                }\n            }\n\n            args.IsValid = isUnique;\n        }\n\n        protected void Button1_Click(object sender, EventArgs e)\n        {\n            // This will trigger the CustomValidator1_ServerValidate method.\n            Page.Validate();\n            \n            if (Page.IsValid)\n            {\n                // Proceed with logic if all validations are successful.\n            }\n        }\n    }\n}