using (SqlConnection cn = new SqlConnection(ConfigurationManager.ConnectionStrings["Northwind"].ConnectionString))\n{\n    string selectSql = "SELECT * FROM tbh_Categories WHERE CategoryID = @CategoryID";\n    SqlDataAdapter adapter = new SqlDataAdapter(selectSql, cn);\n    adapter.SelectCommand.Parameters.Add("@CategoryID", SqlDbType.Int).Value = category.ID;\n\n    // Create a command builder to generate SQL update, insert, and delete commands based on the select command\n    SqlCommandBuilder commandBuilder = new SqlCommandBuilder(adapter);\n\n    DataSet dataSet = new DataSet();\n    adapter.Fill(dataSet, "Categories");\n\n    // Modify the data in the DataSet\n    if (dataSet.Tables["Categories"].Rows.Count > 0)\n    {\n        DataRow row = dataSet.Tables["Categories"].Rows[0];\n        row["Title"] = category.Title;\n        row["Description"] = category.Description;\n    }\n\n    // Update the database with the changes made to the DataSet\n    adapter.Update(dataSet, "Categories");\n}