using System;\nusing System.IO;\nusing System.Runtime.InteropServices;\n\nnamespace YourNamespace\n{\n    public class CentralInstaller\n    {\n        public static void RegisterAllDlls(string folderPath)\n        {\n            foreach (string dll in Directory.GetFiles(folderPath, "*.dll"))\n            {\n                RegisterDll(dll);\n            }\n        }\n\n        private static void RegisterDll(string dllPath)\n        {\n            try\n            {\n                var regAsm = new RegistrationServices();\n                var assembly = System.Reflection.Assembly.LoadFile(dllPath);\n                if (!regAsm.RegisterAssembly(assembly, AssemblyRegistrationFlags.SetCodeBase))\n                {\n                    throw new InvalidOperationException("Failed to register assembly: " + dllPath);\n                }\n                Console.WriteLine($"Successfully registered {dllPath}");\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine($"Error registering {dllPath}: {ex.Message}");\n            }\n        }\n    }\n}