using System;\nusing System.DirectoryServices;\n\npublic class LdapQuery\n{\n    public static void GetGroupMembers(string groupName)\n    {\n        DirectoryEntry dirEnt = new DirectoryEntry();\n        \n        // Search for the specific group\n        using (DirectorySearcher srch = new DirectorySearcher(dirEnt, $"(cn={groupName})"))\n        {\n            srch.Filter = $"(&(objectClass=group)(cn={groupName}))";\n            srch.PageSize = 1000;\n            SearchResult result = srch.FindOne();\n\n            if (result != null)\n            {\n                DirectoryEntry group = result.GetDirectoryEntry();\n                PropertyValueCollection members = group.Properties["member"];\n                \n                foreach (string memberDn in members)\n                {\n                    Console.WriteLine(memberDn);\n                }\n            }\n            else\n            {\n                Console.WriteLine("Group not found.");\n            }\n        }\n    }\n\n    public static void Main(string[] args)\n    {\n        string groupName = "Administrators"; // Change this to the desired group name\n        GetGroupMembers(groupName);\n    }\n}