using (var db = new BloggingContext())\n    {\n        // Create\n        var blog = new Blog { Url = "http://sample.com" };\n        db.Blogs.Add(blog);\n        db.SaveChanges();\n\n        // Read\n        var blogs = db.Blogs.ToList();\n\n        // Update\n        blog.Url = "http://updatedsample.com";\n        db.SaveChanges();\n\n        // Delete\n        db.Blogs.Remove(blog);\n        db.SaveChanges();\n    }