using System;\nusing System.Threading;\nusing System.Web;\n\npublic class LongRunningTaskHandler : IHttpHandler\n{\n    public void ProcessRequest(HttpContext context)\n    {\n        // Start a new thread to handle the long-running task\n        ThreadPool.QueueUserWorkItem(new WaitCallback(LongRunningTask), context);\n        \n        // Respond immediately to the client\n        context.Response.ContentType = "text/plain";\n        context.Response.Write("Task started.");\n    }\n\n    private void LongRunningTask(object state)\n    {\n        HttpContext context = (HttpContext)state;\n        \n        // Perform the long-running task here\n        // ...\n\n        // Ensure thread safety and proper exception handling\n        try\n        {\n            // Example long-running task\n            Thread.Sleep(10000); // Simulate long-running task with a 10 second delay\n        }\n        catch (Exception ex)\n        {\n            // Handle exceptions appropriately\n            // Log the exception\n        }\n    }\n\n    public bool IsReusable\n    {\n        get { return false; }\n    }\n}