string sPath = @"C:\path\to\your\directory";\nstring connectionString = $"Provider=Microsoft.Jet.OLEDB.4.0;Data Source={sPath};Extended Properties=\"text;HDR=Yes;FMT=Delimited\"";\n\nusing (var connection = new OleDbConnection(connectionString))\n{\n    var command = new OleDbCommand("SELECT * FROM [CommaDelimited.txt]", connection);\n    var adapter = new OleDbDataAdapter(command);\n    var dataTable = new DataTable();\n    adapter.Fill(dataTable);\n\n    // Now your dataTable should have the full content of the Notes column without truncation.\n}