using System;\nusing System.Security.Cryptography;\nusing System.Text;\n\npublic class MembershipHelper\n{\n    public static string EncodePassword(string password, string salt)\n    {\n        byte[] passwordBytes = Encoding.Unicode.GetBytes(password);\n        byte[] saltBytes = Convert.FromBase64String(salt);\n        byte[] dst = new byte[passwordBytes.Length + saltBytes.Length];\n\n        Buffer.BlockCopy(passwordBytes, 0, dst, 0, passwordBytes.Length);\n        Buffer.BlockCopy(saltBytes, 0, dst, passwordBytes.Length, saltBytes.Length);\n\n        using (HashAlgorithm algorithm = HashAlgorithm.Create("SHA1"))\n        {\n            byte[] hashBytes = algorithm.ComputeHash(dst);\n            return Convert.ToBase64String(hashBytes);\n        }\n    }\n\n    public static string CreateSalt(int size)\n    {\n        byte[] saltBytes = new byte[size];\n        using (RNGCryptoServiceProvider rng = new RNGCryptoServiceProvider())\n        {\n            rng.GetBytes(saltBytes);\n        }\n        return Convert.ToBase64String(saltBytes);\n    }\n}\n\n// Usage\npublic class Program\n{\n    public static void Main()\n    {\n        string password = "mysecretpassword";\n        string salt = MembershipHelper.CreateSalt(16); // 128 bits\n\n        string encodedPassword = MembershipHelper.EncodePassword(password, salt);\n\n        Console.WriteLine("Salt: " + salt);\n        Console.WriteLine("Encoded Password: " + encodedPassword);\n    }\n}