// In your Default.aspx.cs or any server-side code-behind file\npublic partial class _Default : Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        if (!IsPostBack)\n        {\n            string token = AntiForgery.GetTokens(null, out string cookieToken);\n            ViewState["AntiForgeryToken"] = token;\n            Response.Cookies.Add(new HttpCookie("AntiForgeryCookie", cookieToken));\n        }\n    }\n\n    [WebMethod]\n    [ScriptMethod(ResponseFormat = ResponseFormat.Json)]\n    public static int ValidateLogin(string arg, string token)\n    {\n        AntiForgery.Validate(HttpContext.Current.Request.Cookies["AntiForgeryCookie"].Value, token);\n        // Your validation logic here\n        return 1; // or 0 based on your validation logic\n    }\n}