using Microsoft.PointOfService;\n\npublic class PosPrinterExample\n{\n    public void PrintReceipt()\n    {\n        PosExplorer explorer = new PosExplorer();\n        DeviceInfo deviceInfo = explorer.GetDevice(DeviceType.PosPrinter, "PrinterName"); // Replace "PrinterName" with your printer's name\n\n        if (deviceInfo != null)\n        {\n            PosPrinter printer = (PosPrinter)explorer.CreateInstance(deviceInfo);\n            printer.Open();\n            printer.Claim(1000);\n            printer.DeviceEnabled = true;\n\n            printer.PrintNormal(PrinterStation.Receipt, "Hello, World!\n");\n\n            printer.Release();\n            printer.Close();\n        }\n        else\n        {\n            Console.WriteLine("POS Printer not found.");\n        }\n    }\n}