using System;\nusing Antlr4.Runtime;\nusing Antlr4.Runtime.Tree;\n\npublic class SQLParserExample\n{\n    public static void Main(string[] args)\n    {\n        // Sample SQL code\n        string sqlCode = "SELECT * FROM users WHERE age > 30";\n\n        // Create an input stream from the SQL code\n        AntlrInputStream inputStream = new AntlrInputStream(sqlCode);\n\n        // Create a lexer instance\n        SQLLexer lexer = new SQLLexer(inputStream);\n\n        // Create a token stream from the lexer\n        CommonTokenStream tokenStream = new CommonTokenStream(lexer);\n\n        // Create a parser instance\n        SQLParser parser = new SQLParser(tokenStream);\n\n        // Parse the SQL code starting from the root rule (e.g., sqlStatement)\n        IParseTree tree = parser.sqlStatement();\n\n        // Print the parse tree (you can also traverse it to extract specific information)\n        Console.WriteLine(tree.ToStringTree(parser));\n    }\n}