public void CallWcfService()\n{\n    MyServiceClient client = null;\n    try\n    {\n        client = new MyServiceClient();\n        // Use the client for service operations\n        var result = client.MyOperation();\n    }\n    catch (Exception ex)\n    {\n        // Handle exceptions\n    }\n    finally\n    {\n        if (client != null)\n        {\n            try\n            {\n                client.Close();\n            }\n            catch\n            {\n                client.Abort();\n            }\n        }\n    }\n}