public class Collection<T>\n{\n    //...\n\n    public bool Equals<U>(Collection<T> other, Func<U, U, bool> eq) where T : U\n    {\n        // code using eq delegate to test equality between\n        // members of this and other collection\n        for (int i = 0; i < this.Items.Count; i++)\n        {\n            if (!eq(this.Items[i], other.Items[i]))\n            {\n                return false;\n            }\n        }\n        return true;\n    }\n\n    // This is a placeholder for your collection items\n    private List<T> Items = new List<T>();\n}