using System.Diagnostics;\nusing PdfSharp.Drawing;\nusing PdfSharp.Pdf;\n\npublic void InsertHtmlToPdf(string htmlContent, string pdfFilePath)\n{\n    // Save HTML content to a temporary file\n    string tempHtmlFile = System.IO.Path.GetTempFileName() + ".html";\n    System.IO.File.WriteAllText(tempHtmlFile, htmlContent);\n\n    // Convert HTML to Image using wkhtmltoimage\n    string tempImageFile = System.IO.Path.GetTempFileName() + ".png";\n    ProcessStartInfo psi = new ProcessStartInfo\n    {\n        FileName = "wkhtmltoimage",\n        Arguments = $"--quality 100 {tempHtmlFile} {tempImageFile}",\n        UseShellExecute = false,\n        CreateNoWindow = true\n    };\n    Process process = Process.Start(psi);\n    process.WaitForExit();\n\n    // Create PDF and insert the image\n    using (PdfDocument document = new PdfDocument())\n    {\n        PdfPage page = document.AddPage();\n        XGraphics gfx = XGraphics.FromPdfPage(page);\n        XImage image = XImage.FromFile(tempImageFile);\n        gfx.DrawImage(image, 0, 0, page.Width, page.Height);\n\n        document.Save(pdfFilePath);\n    }\n\n    // Clean up temporary files\n    System.IO.File.Delete(tempHtmlFile);\n    System.IO.File.Delete(tempImageFile);\n}