public class Item1\n{\n    public int Id { get; set; }\n    public string Name { get; set; }\n}\n\npublic class Item2\n{\n    public int Id { get; set; }\n    public string Description { get; set; }\n}\n\nList<Item1> list1 = new List<Item1>\n{\n    new Item1 { Id = 1, Name = "Item1" },\n    new Item1 { Id = 2, Name = "Item2" },\n    new Item1 { Id = 3, Name = "Item3" }\n};\n\nList<Item2> list2 = new List<Item2>\n{\n    new Item2 { Id = 2, Description = "Description2" },\n    new Item2 { Id = 3, Description = "Description3" }\n};\n\n// Select items in list1 that aren't contained in list2 based on Id comparison\nvar result = list1.Where(t => !list2.Any(s => s.Id == t.Id)).ToList();\n\nforeach (var item in result)\n{\n    Console.WriteLine($"Id: {item.Id}, Name: {item.Name}");\n}