using System;\nusing System.Security.Cryptography;\nusing System.Text;\n\npublic class SerialNumberVerifier\n{\n    public static bool VerifySerialNumber(string publicKey, string serialNumber, string userData)\n    {\n        using (RSACryptoServiceProvider rsa = new RSACryptoServiceProvider())\n        {\n            rsa.ImportCspBlob(Convert.FromBase64String(publicKey));\n\n            string dataToVerify = $"{userData}|{DateTime.UtcNow}";\n            byte[] dataBytes = Encoding.UTF8.GetBytes(dataToVerify);\n            byte[] signature = Convert.FromBase64String(serialNumber);\n\n            return rsa.VerifyData(dataBytes, new SHA256CryptoServiceProvider(), signature);\n        }\n    }\n}