using System.Windows.Input;\nusing System.Windows;\n\npublic class MainViewModel\n{\n    #region DelegateCommand: HandleError\n    private DelegateCommand handleErrorCommand;\n\n    public ICommand HandleErrorCommand\n    {\n        get\n        {\n            if (handleErrorCommand == null)\n            {\n                handleErrorCommand = new DelegateCommand(HandleError, CanHandleError);\n            }\n            return handleErrorCommand;\n        }\n    }\n\n    private void HandleError(object parameter)\n    {\n        if (parameter is ValidationErrorEventArgs errorEventArgs)\n        {\n            // Handle the validation error here\n            MessageBox.Show("Validation error in view model: " + errorEventArgs.Error.ErrorContent);\n        }\n    }\n\n    private bool CanHandleError(object parameter)\n    {\n        return true;\n    }\n    #endregion\n}