using System;\nusing System.Runtime.InteropServices;\n\n[ComVisible(true)]\n[Guid("YOUR_GUID_HERE")]\n[InterfaceType(ComInterfaceType.InterfaceIsIDispatch)]\npublic interface IMyClass\n{\n    object GetNestedArray();\n}\n\n[ComVisible(true)]\n[Guid("YOUR_OTHER_GUID_HERE")]\n[ClassInterface(ClassInterfaceType.None)]\npublic class MyClass : IMyClass\n{\n    public object GetNestedArray()\n    {\n        string[][] nestedArray = new string[][]\n        {\n            new string[] { "A1", "B1" },\n            new string[] { "A2", "B2" }\n        };\n\n        // Convert to a COM-compatible object\n        object[] comCompatibleArray = new object[nestedArray.Length];\n        for (int i = 0; i < nestedArray.Length; i++)\n        {\n            comCompatibleArray[i] = nestedArray[i];\n        }\n\n        return comCompatibleArray;\n    }\n}