using System;\nusing Twilio;\nusing Twilio.Rest.Fax.V1;\nusing Twilio.Types;\n\nnamespace SendFax\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            // Your Account SID and Auth Token from twilio.com/console\n            const string accountSid = "your_account_sid";\n            const string authToken = "your_auth_token";\n\n            // Initialize Twilio client\n            TwilioClient.Init(accountSid, authToken);\n\n            // Send a fax\n            var fax = FaxResource.Create(\n                to: new PhoneNumber("fax_number"), // Recipient's fax number\n                from: new PhoneNumber("your_twilio_fax_number"), // Your Twilio fax number\n                mediaUrl: new Uri("http://www.example.com/your-fax-document.pdf") // URL of the PDF document\n            );\n\n            Console.WriteLine($"Fax SID: {fax.Sid}");\n        }\n    }\n}