using System;\nusing System.Security.Cryptography;\nusing System.Text;\n\npublic class PasswordManager\n{\n    public static byte[] EncryptString(string plainText)\n    {\n        return ProtectedData.Protect(Encoding.UTF8.GetBytes(plainText), null, DataProtectionScope.CurrentUser);\n    }\n\n    public static string DecryptString(byte[] cipherText)\n    {\n        return Encoding.UTF8.GetString(ProtectedData.Unprotect(cipherText, null, DataProtectionScope.CurrentUser));\n    }\n    \n    public static void Main()\n    {\n        string password = "MySecurePassword";\n        byte[] encryptedPassword = EncryptString(password);\n        string decryptedPassword = DecryptString(encryptedPassword);\n\n        Console.WriteLine("Encrypted Password: " + Convert.ToBase64String(encryptedPassword));\n        Console.WriteLine("Decrypted Password: " + decryptedPassword);\n    }\n}