using System;\nusing System.Data.SqlClient;\nusing System.Threading;\n\npublic class DatabaseConnection\n{\n    private const int MaxRetryCount = 5;\n    private const int RetryDelayMilliseconds = 2000; // 2 seconds\n\n    public void ConnectToDatabase(string connectionString)\n    {\n        int retryCount = 0;\n\n        while (retryCount < MaxRetryCount)\n        {\n            try\n            {\n                using (SqlConnection connection = new SqlConnection(connectionString))\n                {\n                    connection.Open();\n                    // Perform database operations\n                }\n\n                // Exit the loop if connection is successful\n                break;\n            }\n            catch (SqlException ex) when (ex.Message.Contains("An existing connection was forcibly closed by the remote host"))\n            {\n                retryCount++;\n                if (retryCount >= MaxRetryCount)\n                {\n                    throw; // Rethrow the exception after max retries exceeded\n                }\n\n                // Wait before retrying\n                Thread.Sleep(RetryDelayMilliseconds);\n            }\n        }\n    }\n}