using Aurigma.GraphicsMill;\nusing Aurigma.GraphicsMill.Transforms;\n\npublic static double CalculateImageNoise(Bitmap bitmap)\n{\n    // Convert the image to grayscale for simplicity\n    var grayscaleBitmap = bitmap.Clone();\n    grayscaleBitmap.ColorManagement.ConvertToGrayscale();\n\n    // Apply a Gaussian blur to the image\n    var blurredBitmap = grayscaleBitmap.Clone();\n    blurredBitmap.Transforms.GaussianBlur(3);\n\n    // Calculate the difference between the original and blurred images\n    var differenceBitmap = grayscaleBitmap.Clone();\n    differenceBitmap.Transforms.Subtract(blurredBitmap);\n\n    // Calculate the standard deviation of the difference image\n    double noiseLevel = differenceBitmap.Statistics.StandardDeviation();\n\n    return noiseLevel;\n}