@Ajax.ActionLink(\n    "Update Content",           // Link text\n    "UpdateAction",             // Action method\n    new { id = "someId" },      // Route values\n    new AjaxOptions\n    {\n        UpdateTargetId = "contentDiv",  // The id of the div to update\n        InsertionMode = InsertionMode.Replace,\n        HttpMethod = "GET"\n    },\n    new { href = Url.Action("UpdateAction", new { id = "someId" }) } // HTML attributes\n)