// Assuming you have fetched data from the database into a DataTable\nDataTable dataTable = GetDataFromDatabase();\n\nforeach (DataRow row in dataTable.Rows)\n{\n    string myColumnValue = row["MyColumn"].ToString();\n    string displayValue;\n    string style;\n\n    switch (myColumnValue)\n    {\n        case "Y":\n            displayValue = "Yes";\n            style = "background-color:pink";\n            break;\n        case "N":\n            displayValue = "No";\n            style = string.Empty;\n            break;\n        default:\n            displayValue = "Unknown";\n            style = string.Empty;\n            break;\n    }\n\n    // Use displayValue and style in your presentation layer\n}